export const generateDecoratedMintNavigationFromPages = (filenamePageMetadataMap, mintConfigNav) => {
    const filenames = Object.keys(filenamePageMetadataMap);
    const createNav = (nav) => {
        return Object.assign(Object.assign({}, nav), { pages: nav.pages.map((page) => {
                if (typeof page === 'string') {
                    if (filenames.includes(page)) {
                        return filenamePageMetadataMap[page];
                    }
                    else {
                        console.log(`⚠️   "${page}" is defined in the mint.json navigation but the file does not exist.`);
                        return {
                            title: slugToTitle(page),
                            href: optionallyAddLeadingSlash(page),
                        };
                    }
                }
                return createNav(page);
            }) });
    };
    return mintConfigNav.map((nav) => createNav(nav));
};
export const slugToTitle = (slug) => {
    const slugArr = slug.split('/');
    let defaultTitle = slugArr[slugArr.length - 1].split('-').join(' '); //replace all dashes
    defaultTitle = defaultTitle.split('_').join(' '); //replace all underscores
    defaultTitle = defaultTitle.charAt(0).toUpperCase() + defaultTitle.slice(1); //capitalize first letter
    return defaultTitle;
};
export function optionallyAddLeadingSlash(path) {
    if (path.startsWith('/')) {
        return path;
    }
    return '/' + path;
}
